import { useEffect,useState } from 'react';

import { useRouter, useLocalSearchParams, useNavigation } from 'expo-router';

import { IconSymbol } from '../components/ui/IconSymbol';

import { SearchBar } from 'react-native-screens';
import { Ionicons } from '@expo/vector-icons'; // 使用图标库
// 模拟商品数据
const products = [
  { id: '1', name: 'Tomato', price: '$5.99 / lb', image: 'data:image/jpeg;base64,' },
  { id: '2', name: 'Tomato', price: '$5.99 / lb', image: 'data:image/jpeg;base64,' },
  { id: '3', name: 'Tomato', price: '$5.99 / lb', image: 'data:image/jpeg;base64,' },
  { id: '4', name: 'Tomato', price: '$5.99 / lb', image: 'data:image/jpeg;base64,' },
  { id: '5', name: 'Tomato', price: '$5.99 / lb', image: 'data:image/jpeg;base64,' },
  { id: '6', name: 'Tomato', price: '$5.99 / lb', image: 'data:image/jpeg;base64,' },
];


const ShopNow = () => {
  // 渲染单个商品项
  const renderProduct = ({ item }) => (
    <View style={styles.productCard}>
      <Image source={{ uri: item.image }} style={styles.productImage} />
      <Text style={styles.productName}>{item.name}</Text>
      <Text style={styles.productPrice}>{item.price}</Text>
    </View>
  );
  const navigation = useNavigation();
  const router = useRouter();
  const [searchText, setSearchText] = useState('');
  useEffect(() => {
        navigation.setOptions({
          header: () => (
            <SafeAreaView>
              <View style={styles.header}>
                <TouchableOpacity onPress={() => router.back()} style={styles.customBackButton}>
                  <IconSymbol name="chevron.left" size={24} color="#000" />
                </TouchableOpacity>
                <Text style={styles.headerTitle}>Shop Now</Text>
              </View>
            </SafeAreaView>
          ),
        });
      }, [navigation, router]);
  return (
    <SafeAreaView style={styles.container}>
    <View style={styles.searchBar}>
        <Ionicons name="search" size={20} color="#888" style={styles.icon} />
        <TextInput
          style={styles.input}
          placeholder="Search"
          placeholderTextColor="#888"
          value={searchText}
          onChangeText={setSearchText}
        />
      </View>
      <FlatList
        data={products}
        renderItem={renderProduct}
        keyExtractor={(item) => item.id}
        numColumns={2} // 每行显示2个商品
        contentContainerStyle={styles.listContainer}
      />
    </SafeAreaView>
  );
};

